mapper.select:
select

  distinct


from smellcount




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select

from smellcount
where commitHash = #{commithash,jdbcType=VARCHAR}
  and projURL = #{projurl,jdbcType=VARCHAR}

mapper.delete:
delete from smellcount
where commitHash = #{commithash,jdbcType=VARCHAR}
  and projURL = #{projurl,jdbcType=VARCHAR}
